mapper.update:
update order_delivery_detail
set out_stock_time = #{outStockTime}
where order_id = #{orderId}

update order_delivery_detail
set deliverer_no    = #{delivererNo},
    deliverer_name  = #{delivererName},
    deliverer_phone = #{delivererPhone}
where order_id = #{orderId}

update order_delivery_detail
set signed_time = #{signedTime}
where order_id = #{orderId}
